import React from "react";
import Titles from "./components/Titles";
import Form from "./components/Form";
import Weather from "./components/Weather";

const API_KEY = "2e863c191c07d3aeff1f0ea8a4ed6003";

class App extends React.Component {
  //OLD WAY
  // constructor(){
  //   this.getWeather = this.getWeather.bind(this);
  // }
  // getWeather(){

  // }
  // OUR CALL TO THE API TO GET THE WEATHER FROM OPENWEATHER
  getWeather = async () => {
    //Allow you to use .this indepently
    const api_call = await fetch(
      `https://api.openweathermap.org/data/2.5/weather?q=Manchester,uk&appid=${API_KEY}&units=metric`
    );
    const data = await api_call.json();
    console.log(data);
  };

  render() {
    return (
      <div>
        <Titles />
        <Form getWeather={this.getWeather} /> //a
        <Weather />
      </div>
    );
  }
}

export default App; //allows it to be imported elsewhere
