import React from "react";
import Titles from "./components/Titles";
import Form from "./components/Form";
import Weather from "./components/Weather";

const API_KEY = "2e863c191c07d3aeff1f0ea8a4ed6003";

class App extends React.Component {
  state = {
    temperature: undefined,
    city: undefined,
    country: undefined,
    humidity: undefined,
    description: undefined,
    error: undefined,
  };

  //OLD WAY
  // constructor(){
  //   this.getWeather = this.getWeather.bind(this);
  // }
  // getWeather(){

  // }
  // OUR CALL TO THE API TO GET THE WEATHER FROM OPENWEATHER

  getWeather = async (e) => {
    e.preventDefault(); //Going to prevent defaults behaviour. NO REFRESH SEE CONTENT ON CONSOLE

    const city = e.target.elements.city.value;
    const country = e.target.elements.country.value;
    //Allow you to use .this indepently
    const api_call = await fetch(
      `https://api.openweathermap.org/data/2.5/weather?q=${city},${country}&appid=${API_KEY}&units=metric`
    );
    const data = await api_call.json();
    console.log(data);

    //STATE vars instead of this.name = name; we can do it like that
    this.setState({
      temperature: data.main.temp,
      city: data.name,
      country: data.sys.country,
      humidity: data.main.humidity,
      description: data.weather[0].description,
      error: "",
    });
  };

  render() {
    return (
      <div>
        <Titles />
        {/*Allow us to redirect the method to form*/}
        <Form getWeather={this.getWeather} />
        <Weather
          temperature={this.state.temperature}
          city={this.state.city}
          country={this.state.country}
          humidity={this.state.humidity}
          description={this.state.description}
          error={this.state.error}
        />
      </div>
    );
  }
}

export default App; //allows it to be imported elsewhere
